package kk.practice.string丢进intlist;import java.lang.reflect.InvocationTargetException;import java.util.ArrayList;import java.util.List;/** * @author kangkai on 2018/11/13. */public class ByReflect {    public static void main(String[] args) throws NoSuchMethodException, InvocationTargetException, IllegalAccessException {        List<Integer> list = new ArrayList<>(10){{this.add(123);}};        list.getClass().getMethod("add",Object.class).invoke(list,"adc");        System.out.println(list);    }}