package kk.practice.调用方法时构造类;import kk.practise.Java反射机制.UserInfo;/** * @author kangkai on 2018/5/3. */public class Demo01 {    public static void main(String[] args) {        UserInfo userInfo = new UserInfo();        userInfo.setUserName("kang");        sysoname(userInfo);        sysoname(new UserInfo(){{            this.setUserName("kai");        }});    }    private static void sysoname(UserInfo userInfo) {        System.out.println(userInfo.getUserName());    }}