package kk.practise.值传递与引用传递;import kk.practise.Map测试.X;/** * @author kangkai on 2018/9/17. */public class update917 {    public static void main(String[] args) {        testInt();        testString();        testArray();        testX();        testX02();    }    public static void testInt() {        int a = 1;        int b = a;        a = 2;        System.out.println("b = "+b);        changeInt(a);        System.out.println("a = "+a);    }    public static void testString() {        String a = "1";        String b = a;        a = "2";        System.out.println("b = "+b);        changeString(a);        System.out.println("a = "+a);    }    public static void testArray() {        int[] a = {1,2,3};        int[] b = a;        a[0] = 2;        System.out.println("b[0] = "+b[0]);        changeArray(a);        System.out.println("a[0] = "+a[0]);    }    public static void testX() {        X a = new X("1");        X b = a;        a.setValue("2");        System.out.println("b.value = "+b.getValue());        changeX(a);        System.out.println("a.value = "+a.getValue());    }    public static void testX02() {        X a = new X("1");        changeX02(a);        System.out.println("a.value = "+a.getValue());    }    static void changeInt(int a) { a = 100;}    static void changeString(String a) { a = "100";}    static void changeArray(int[] a) { a[0] = 100;}    static void changeX(X a) { a.setValue("100");}    static void changeX02(X a) {        a = new X("");        a.setValue("100");    }}