package kk.practise.反射赋值;/** * @author kangkai on 2018/4/3. */import java.io.Serializable;import java.util.List;public class UserPushSave implements Serializable{private static final long serialVersionUID = -2765044255480754188L;private String pushTitle;private String pushIssueTime_String;private String pushSendTime_String;    private List<UserInfo> userInfoList;    public String getPushTitle() {        return pushTitle;    }    public void setPushTitle(String pushTitle) {        this.pushTitle = pushTitle;    }    public List<UserInfo> getUserInfoList() {        return userInfoList;    }    public void setUserInfoList(List<UserInfo> userInfoList) {        this.userInfoList = userInfoList;    }    public String getPushIssueTime_String() {        return pushIssueTime_String;    }    public void setPushIssueTime_String(String pushIssueTime_String) {        this.pushIssueTime_String = pushIssueTime_String;    }    public String getPushSendTime_String() {        return pushSendTime_String;    }    public void setPushSendTime_String(String pushSendTime_String) {        this.pushSendTime_String = pushSendTime_String;    }    static class UserInfo{        private String pushUserName;        private String pushUserSex;        private String pushUserNum;        private String specialPushContent;        public String getPushUserName() {            return pushUserName;        }        public void setPushUserName(String pushUserName) {            this.pushUserName = pushUserName;        }        public String getPushUserSex() {            return pushUserSex;        }        public void setPushUserSex(String pushUserSex) {            this.pushUserSex = pushUserSex;        }        public String getPushUserNum() {            return pushUserNum;        }        public void setPushUserNum(String pushUserNum) {            this.pushUserNum = pushUserNum;        }        public String getSpecialPushContent() {            return specialPushContent;        }        public void setSpecialPushContent(String specialPushContent) {            this.specialPushContent = specialPushContent;        }        @Override        public String toString() {            return "UserInfo{" +                    "pushUserName='" + pushUserName + '\'' +                    ", pushUserSex='" + pushUserSex + '\'' +                    ", pushUserNum='" + pushUserNum + '\'' +                    ", specialPushContent='" + specialPushContent + '\'' +                    '}';        }    }}